import React from 'react'
import ReactDOM from 'react-dom'

const Part = ({ name, key }) => {
  return (
    <div>
      {name}
    </div>
  )
}

const Course = ({ course }) => {
  console.log({course})
    const body = () => course.parts.map(part =>
      <Part
      name={course.parts}
      key={course.parts.id}
      />
    )

    return (
    <div>
      <Header header={course.name} />
      {body()}
    </div>
    )
  }


const Header = (props) => {
  return (
      <h1>{props.header}</h1>
  )
}

const App = () => {
  const course = {
      name: 'Half Stack application development',
      parts: [
        {
          name: 'Fundamentals of React',
          exercises: 10,
          id: 1
        },
        {
          name: 'Using props to pass data',
          exercises: 7,
          id: 2
        },
        {
          name: 'State of a component',
          exercises: 14,
          id: 3
        }
      ]
    }
  return (
    <div>
      <Course course={course} />
    </div>
  )
}


ReactDOM.render(<App />, document.getElementById('root'))
