import React from 'react'
import ReactDOM from 'react-dom'

const Course = (props) => {
  return (
  <div>
    <Header header={props.course.name} />

  </div>
  )
}

const Header = (props) => {
  return (
    <div>
      <h1>{props.name}</h1>
    </div>
  )
}

const Part = (props) => {
  return (
    <div>
       <p>
        {props.part.name} {props.part.exercises}
      </p>
    </div>
  )
}

